$(document).ready(function() {
    // Mouse Events
    $(".click-box").click(function() {
        $(this).text("Tƒ±klandƒ±!")
            .addClass("active");
        setTimeout(() => {
            $(this).text("Tƒ±klama i√ßin bekliyor...")
                .removeClass("active");
        }, 1000);
    });

    $(".dblclick-box").dblclick(function() {
        $(this).text("√áift Tƒ±klandƒ±!")
            .addClass("active");
        setTimeout(() => {
            $(this).text("√áift tƒ±klama i√ßin bekliyor...")
                .removeClass("active");
        }, 1000);
    });

    $(".hover-box")
        .mouseenter(function() {
            $(this).text("Mouse √úzerinde!")
                .addClass("hovered");
        })
        .mouseleave(function() {
            $(this).text("Hover i√ßin bekliyor...")
                .removeClass("hovered");
        });

    // Keyboard Events
    $(".key-input")
        .keydown(function(e) {
            $(".key-status").text(`Tu≈ü Basƒ±ldƒ±: ${e.key}`)
                .addClass("active");
        })
        .keyup(function(e) {
            $(".key-status").text(`Tu≈ü Bƒ±rakƒ±ldƒ±: ${e.key}`)
                .removeClass("active");
            setTimeout(() => {
                $(".key-status").text("Tu≈ü bekliyor...");
            }, 1000);
        });

    // Form Events
    $(".test-form")
        .submit(function(e) {
            e.preventDefault();
            $(".form-status").text("Form G√∂nderildi!")
                .addClass("success")
                .fadeIn();
            setTimeout(() => {
                $(".form-status").fadeOut(function() {
                    $(this).removeClass("success");
                });
            }, 2000);
        });

    $(".test-input")
        .focus(function() {
            $(".focus-status").text("Input Odaklandƒ±")
                .addClass("focused");
        })
        .blur(function() {
            $(".focus-status").text("Input Odak Kaybetti")
                .removeClass("focused");
        })
        .change(function() {
            $(".change-status").text(`Deƒüer Deƒüi≈üti: ${$(this).val()}`)
                .addClass("changed");
            setTimeout(() => {
                $(".change-status").removeClass("changed");
            }, 1000);
        });

    // Window Events
    let resizeTimeout;
    $(window).resize(function() {
        clearTimeout(resizeTimeout);
        $(".window-status").text("Pencere Boyutu Deƒüi≈üiyor...")
            .addClass("active");

        resizeTimeout = setTimeout(() => {
            $(".window-status").text(
                `Yeni Boyut: ${window.innerWidth}x${window.innerHeight}`
            ).removeClass("active");
        }, 500);
    });

    let scrollTimeout;
    $(".events-demo-container").scroll(function() {
        clearTimeout(scrollTimeout);
        $(".scroll-status").text("Sayfa Kaydƒ±rƒ±lƒ±yor...")
            .addClass("active");

        scrollTimeout = setTimeout(() => {
            $(".scroll-status").text(
                `Scroll Pozisyonu: ${$(this).scrollTop()}px`
            ).removeClass("active");
        }, 500);
    });
});

