$(document).ready(function() {
    // √ñrnek JSON verisi
    const jsonData = {
        "ogrenciler": [
            {"ad": "Ali", "not": 85, "dersler": ["Matematik", "Fizik"]},
            {"ad": "Ay≈üe", "not": 92, "dersler": ["Kimya", "Biyoloji"]},
            {"ad": "Mehmet", "not": 78, "dersler": ["Tarih", "Coƒürafya"]}
        ]
    };

    // JSON verisini g√∂r√ºnt√ºle
    $('#rawJson').text(JSON.stringify(jsonData, null, 2));

    // JSON verisini i≈üle ve tabloya ekle
    jsonData.ogrenciler.forEach(function(ogrenci) {
        const row = `
            <tr>
                <td>${ogrenci.ad}</td>
                <td>${ogrenci.not}</td>
                <td>${ogrenci.dersler.join(", ")}</td>
            </tr>
        `;
        $('#jsonTable tbody').append(row);
    });

    // JSON d√∂n√º≈üt√ºrme √∂rnekleri
    $('#convertToJson').click(function() {
        const formData = {
            ad: $('#studentName').val(),
            not: parseInt($('#studentGrade').val()),
            dersler: $('#studentCourses').val().split(',')
                .map(ders => ders.trim())
                .filter(ders => ders !== '')
        };

        $('#jsonResult').text(JSON.stringify(formData, null, 2))
            .parent().slideDown();
    });
});

