$(document).ready(function() {
    // AJAX Durum G√∂stergesi
    $(document).ajaxStart(function() {
        updateStatus('ƒ∞stek g√∂nderiliyor...', 'pending');
    }).ajaxSuccess(function() {
        updateStatus('ƒ∞stek ba≈üarƒ±lƒ±!', 'success');
    }).ajaxError(function() {
        updateStatus('Hata olu≈ütu!', 'error');
    }).ajaxComplete(function() {
        setTimeout(() => {
            updateStatus('Hazƒ±r', 'ready');
        }, 2000);
    });

    // GET ƒ∞steƒüi
    $('#getDataBtn').click(function() {
        $.ajax({
            url: 'https://jsonplaceholder.typicode.com/posts/1',
            method: 'GET',
            dataType: 'json'
        })
        .done(function(response) {
            const html = `
                <div class="result-item success">
                    <h5>${response.title}</h5>
                    <p>${response.body}</p>
                    <small>Post ID: ${response.id}</small>
                </div>
            `;
            $('#getResults').prepend(html);
        })
        .fail(function(xhr, status, error) {
            showError('#getResults', error);
        });
    });

    // POST ƒ∞steƒüi
    $('#postDataBtn').click(function() {
        const data = {
            title: $('#titleInput').val(),
            body: $('#bodyInput').val(),
            userId: 1
        };

        $.ajax({
            url: 'https://jsonplaceholder.typicode.com/posts',
            method: 'POST',
            data: JSON.stringify(data),
            contentType: 'application/json',
            dataType: 'json'
        })
        .done(function(response) {
            const html = `
                <div class="result-item success">
                    <h5>G√∂nderilen Veri:</h5>
                    <p><strong>Ba≈ülƒ±k:</strong> ${response.title}</p>
                    <p><strong>ƒ∞√ßerik:</strong> ${response.body}</p>
                    <small>Yeni Post ID: ${response.id}</small>
                </div>
            `;
            $('#postResults').prepend(html);

            // Formu temizle
            $('#titleInput').val('');
            $('#bodyInput').val('');
        })
        .fail(function(xhr, status, error) {
            showError('#postResults', error);
        });
    });

    // Sonu√ßlarƒ± Temizleme
    $('#clearGetResults').click(function() {
        $('#getResults').empty();
    });

    $('#clearPostResults').click(function() {
        $('#postResults').empty();
    });

    // Yardƒ±mcƒ± Fonksiyonlar
    function showError(target, error) {
        const html = `
            <div class="result-item error">
                <p class="error-message">Hata: ${error}</p>
            </div>
        `;
        $(target).prepend(html);
    }

    function updateStatus(message, status) {
        const $status = $('#ajaxStatus');
        $status.attr('class', 'status-panel ' + status)
            .find('.status-text').text(message);
    }
});

