cache.js:
$(document).ready(function() {
    // Cache mekanizmasƒ±
    const cache = {
        data: {},
        timeout: 10 * 1000, // Demo i√ßin 10 saniye

        set: function(key, value) {
            this.data[key] = {
                value: value,
                timestamp: Date.now()
            };
            updateCacheStatus();
        },

        get: function(key) {
            const item = this.data[key];
            if (!item) return null;

            if (Date.now() - item.timestamp > this.timeout) {
                delete this.data[key];
                updateCacheStatus();
                return null;
            }

            return item.value;
        },

        clear: function() {
            this.data = {};
            updateCacheStatus();
        }
    };

    // Cache durumunu g√ºncelle
    function updateCacheStatus() {
        const cacheContent = Object.keys(cache.data).map(key => {
            const item = cache.data[key];
            const age = Math.round((Date.now() - item.timestamp) / 1000);
            const timeLeft = Math.max(0, 10 - age);

            return `
                <div class="cache-item">
                    <strong>Key:</strong> ${key}
                    <br>
                    <strong>Ya≈ü:</strong> ${age} saniye
                    <br>
                    <strong>Kalan S√ºre:</strong> ${timeLeft} saniye
                </div>
            `;
        }).join("");

        $("#cacheStatus").html(
            cacheContent || "<p class=\"empty-cache\">Cache bo≈ü</p>"
        );
    }

    // Veri getirme fonksiyonu
    function fetchData(endpoint) {
        const url = `https://jsonplaceholder.typicode.com/${endpoint}/1`;
        updateRequestStatus("Kontrol ediliyor...");

        const cachedData = cache.get(url);
        if (cachedData) {
            updateRequestStatus("Cache'den alƒ±ndƒ±!", "cache");
            displayResult(cachedData);
            return;
        }

        updateRequestStatus("Y√ºkleniyor...", "loading");

        $.getJSON(url)
            .done(function(data) {
                cache.set(url, data);
                updateRequestStatus("Yeni veri y√ºklendi!", "success");
                displayResult(data);
            })
            .fail(function(xhr, status, error) {
                updateRequestStatus("Hata: " + error, "error");
            });
    }

    // Sonu√ßlarƒ± g√∂ster
    function displayResult(data) {
        const html = `
            <div class="result-item">
                <pre>${JSON.stringify(data, null, 2)}</pre>
            </div>
        `;
        $("#results").html(html);
    }

    // ƒ∞stek durumunu g√ºncelle
    function updateRequestStatus(message, status = "") {
        $("#requestStatus")
            .attr("class", "status-panel " + status)
            .find(".status-text")
            .text(message);
    }

    // Event listeners
    $("#fetchPost").click(() => fetchData("posts"));
    $("#fetchUser").click(() => fetchData("users"));
    $("#clearCache").click(() => {
        cache.clear();
        $("#results").empty();
        updateRequestStatus("Cache temizlendi", "warning");
    });

    // Cache durumunu periyodik olarak g√ºncelle
    setInterval(updateCacheStatus, 1000);

    // ƒ∞lk durumu g√∂ster
    updateCacheStatus();
    updateRequestStatus("Hazƒ±r");
});

