$(document).ready(function() {
    // Mouse Events
    $(".click-box").click(function() {
        $(this).text("TÄ±klandÄ±!")
            .addClass("active");
        setTimeout(() => {
            $(this).text("TÄ±klama iÃ§in bekliyor...")
                .removeClass("active");
        }, 1000);
    });

    $(".dblclick-box").dblclick(function() {
        $(this).text("Ã‡ift TÄ±klandÄ±!")
            .addClass("active");
        setTimeout(() => {
            $(this).text("Ã‡ift tÄ±klama iÃ§in bekliyor...")
                .removeClass("active");
        }, 1000);
    });

    $(".hover-box")
        .mouseenter(function() {
            $(this).text("Mouse Ãœzerinde!")
                .addClass("hovered");
        })
        .mouseleave(function() {
            $(this).text("Hover iÃ§in bekliyor...")
                .removeClass("hovered");
        });

    // Keyboard Events
    $(".key-input")
        .keydown(function(e) {
            $(".key-status").text(`TuÅŸ BasÄ±ldÄ±: ${e.key}`)
                .addClass("active");
        })
        .keyup(function(e) {
            $(".key-status").text(`TuÅŸ BÄ±rakÄ±ldÄ±: ${e.key}`)
                .removeClass("active");
            setTimeout(() => {
                $(".key-status").text("TuÅŸ bekliyor...");
            }, 1000);
        });

    // Form Events
    $(".test-form")
        .submit(function(e) {
            e.preventDefault();
            $(".form-status").text("Form GÃ¶nderildi!")
                .addClass("success")
                .fadeIn();
            setTimeout(() => {
                $(".form-status").fadeOut(function() {
                    $(this).removeClass("success");
                });
            }, 2000);
        });

    $(".test-input")
        .focus(function() {
            $(".focus-status").text("Input OdaklandÄ±")
                .addClass("focused");
        })
        .blur(function() {
            $(".focus-status").text("Input Odak Kaybetti")
                .removeClass("focused");
        })
        .change(function() {
            $(".change-status").text(`DeÄŸer DeÄŸiÅŸti: ${$(this).val()}`)
                .addClass("changed");
            setTimeout(() => {
                $(".change-status").removeClass("changed");
            }, 1000);
        });

    // Window Events
    let resizeTimeout;
    $(window).resize(function() {
        clearTimeout(resizeTimeout);
        $(".window-status").text("Pencere Boyutu DeÄŸiÅŸiyor...")
            .addClass("active");

        resizeTimeout = setTimeout(() => {
            $(".window-status").text(
                `Yeni Boyut: ${window.innerWidth}x${window.innerHeight}`
            ).removeClass("active");
        }, 500);
    });

    let scrollTimeout;
    $(".events-demo-container").scroll(function() {
        clearTimeout(scrollTimeout);
        $(".scroll-status").text("Sayfa KaydÄ±rÄ±lÄ±yor...")
            .addClass("active");

        scrollTimeout = setTimeout(() => {
            $(".scroll-status").text(
                `Scroll Pozisyonu: ${$(this).scrollTop()}px`
            ).removeClass("active");
        }, 500);
    });
});

