// DOM y√ºklendikten sonra √ßalƒ±≈üacak kodlar
$(document).ready(function() {
    // Sayfa y√ºklendiƒüinde mesaj g√∂ster
    $("#readyMessage").text("DOM y√ºklendi! üöÄ")
        .addClass("ready");

    // Buton tƒ±klamalarƒ±nƒ± dinle
    $("#addElement").click(function() {
        const yeniElement = $("<div>", {
            class: "element",
            text: "Yeni Element " + ($(".element").length + 1)
        });

        $("#elements").append(yeniElement);
        updateCount();
    });

    $("#removeElement").click(function() {
        const elements = $(".element");
        if (elements.length > 0) {
            elements.last().remove();
            updateCount();
        }
    });

    // Element sayƒ±sƒ±nƒ± g√ºncelle
    function updateCount() {
        $("#elementCount").text($(".element").length);
    }
});

