xml.js:
$(document).ready(function() {
    const xmlString = `<?xml version="1.0" encoding="UTF-8"?>
<sinif>
    <ogrenci>
        <ad>Ali</ad>
        <not>85</not>
    </ogrenci>
    <ogrenci>
        <ad>Ay≈üe</ad>
        <not>92</not>
    </ogrenci>
    <ogrenci>
        <ad>Mehmet</ad>
        <not>78</not>
    </ogrenci>
</sinif>`;

    try {
        // XML'i parse et
        const xmlDoc = $.parseXML(xmlString);
        const $xml = $(xmlDoc);

        // √ñƒürencileri listele
        $xml.find('ogrenci').each(function() {
            const $ogrenci = $(this);
            const ad = $ogrenci.find('ad').text();
            const not = $ogrenci.find('not').text();

            const ogrenciHtml = `
                <div class="ogrenci-item">
                    <span class="ad">${ad}</span>
                    <span class="not">${not}</span>
                </div>
            `;

            $('#ogrenciListesi').append(ogrenciHtml);
        });

        $('#xmlStatus').text('XML ba≈üarƒ±yla i≈ülendi!')
            .removeClass('error')
            .addClass('success');

    } catch (error) {
        $('#xmlStatus').text('XML i≈ülenirken hata olu≈ütu: ' + error.message)
            .removeClass('success')
            .addClass('error');
    }
});

