dom_manipulation.js:
$(document).ready(function() {
    let detachedElement = null;
    let counter = 1;

    // √ñƒüe Ekleme ƒ∞≈ülemleri
    $('#appendBtn').click(function() {
        $('#addContainer').append(`<p class="added-item append">Sona eklenen √∂ƒüe ${counter++}</p>`);
    });

    $('#prependBtn').click(function() {
        $('#addContainer').prepend(`<p class="added-item prepend">Ba≈üa eklenen √∂ƒüe ${counter++}</p>`);
    });

    $('#afterBtn').click(function() {
        $('#addContainer').after(`<p class="added-item after">Sonrasƒ±na eklenen √∂ƒüe ${counter++}</p>`);
    });

    $('#beforeBtn').click(function() {
        $('#addContainer').before(`<p class="added-item before">√ñncesine eklenen √∂ƒüe ${counter++}</p>`);
    });

    // √ñƒüe Silme ƒ∞≈ülemleri
    $('#removeContainer').on('click', 'p', function() {
        $(this).toggleClass('selected');
    });

    $('#removeBtn').click(function() {
        $('#removeContainer p.selected').fadeOut(300, function() {
            $(this).remove();
        });
    });

    $('#emptyBtn').click(function() {
        $('#removeContainer').children().fadeOut(300, function() {
            $('#removeContainer').empty();
        });
    });

    $('#detachBtn').click(function() {
        if ($('#removeContainer p').length > 0) {
            detachedElement = $('#removeContainer p:first').detach();
            $('#restoreBtn').prop('disabled', false);
            showNotification('√ñƒüe ge√ßici olarak kaldƒ±rƒ±ldƒ±');
        }
    });

    $('#restoreBtn').click(function() {
        if (detachedElement) {
            $('#removeContainer').prepend(detachedElement);
            detachedElement = null;
            $(this).prop('disabled', true);
            showNotification('√ñƒüe geri getirildi');
        }
    });

    // Bildirim g√∂sterme fonksiyonu
    function showNotification(message) {
        $('.notification').remove(); // √ñnceki bildirimi kaldƒ±r
        $('<div class="notification">' + message + '</div>')
            .appendTo('body')
            .fadeIn(300)
            .delay(2000)
            .fadeOut(300, function() {
                $(this).remove();
            });
    }

    // Ba≈ülangƒ±√ß bilgilendirmesi
    $('<div class="info-message">Silmek istediƒüiniz paragraflarƒ± se√ßmek i√ßin √ºzerlerine tƒ±klayƒ±n.</div>')
        .insertAfter('#removeContainer');
});

