$(document).ready(function() {
    // Resim √ñzellikleri
    let imageCounter = 1;
    const images = [
        'https://picsum.photos/300/200',
        'https://picsum.photos/300/201',
        'https://picsum.photos/300/202'
    ];

    $('#changeImageBtn').click(function() {
        imageCounter = (imageCounter + 1) % images.length;
        $('#demoImage').fadeOut(300, function() {
            $(this).attr('src', images[imageCounter])
                   .attr('alt', `Demo Resim ${imageCounter + 1}`)
                   .fadeIn(300);
        });
    });

    $('#toggleBorderBtn').click(function() {
        const $img = $('#demoImage');
        $img.toggleClass('with-border');

        if ($img.hasClass('with-border')) {
            $img.attr('title', 'Kenarlƒ±klƒ± Resim');
        } else {
            $img.removeAttr('title');
        }
    });

    $('#showImageInfo').click(function() {
        const $img = $('#demoImage');
        const info = {
            'Kaynak (src)': $img.attr('src'),
            'Alternatif Metin (alt)': $img.attr('alt'),
            'Ba≈ülƒ±k (title)': $img.attr('title') || 'Yok',
            'Geni≈ülik': $img.width() + 'px',
            'Y√ºkseklik': $img.height() + 'px'
        };

        showInfo('#imageInfo', info);
    });

    // Form √ñzellikleri
    $('#disableInput').click(function() {
        const $input = $('#demoInput');
        $input.prop('disabled', !$input.prop('disabled'));
        $(this).toggleClass('active');
    });

    $('#readonlyInput').click(function() {
        const $input = $('#demoInput');
        $input.prop('readonly', !$input.prop('readonly'));
        $(this).toggleClass('active');
    });

    $('#toggleCheck').click(function() {
        const $checkbox = $('#demoCheckbox');
        $checkbox.prop('checked', !$checkbox.prop('checked'));
    });

    // √ñzel Veri √ñzellikleri
    let dataCounter = 1;

    $('#addDataBtn').click(function() {
        const $container = $('#dataContainer');
        const newData = `veri${dataCounter}`;
        $container.data('dynamic-' + dataCounter, newData);
        showNotification(`Yeni veri eklendi: dynamic-${dataCounter} = ${newData}`);
        dataCounter++;
    });

    $('#showDataBtn').click(function() {
        const $container = $('#dataContainer');
        const info = {
            'ID': $container.data('id'),
            'Kategori': $container.data('category'),
            'Bilgi': $container.data('info')
        };

        // Dinamik eklenen verileri g√∂ster
        for (let i = 1; i < dataCounter; i++) {
            const key = 'dynamic-' + i;
            info[key] = $container.data(key);
        }

        showInfo('#dataInfo', info);
    });

    // Yardƒ±mcƒ± Fonksiyonlar
    function showInfo(selector, info) {
        const $container = $(selector);
        let html = '<div class="info-content">';

        for (const [key, value] of Object.entries(info)) {
            html += `<div class="info-item">
                        <span class="info-label">${key}:</span>
                        <span class="info-value">${value}</span>
                    </div>`;
        }

        html += '</div>';

        $container.html(html)
                  .hide()
                  .fadeIn(300);
    }

    function showNotification(message) {
        $('.notification').remove();
        $('<div class="notification">' + message + '</div>')
            .appendTo('body')
            .fadeIn(300)
            .delay(2000)
            .fadeOut(300, function() {
                $(this).remove();
            });
    }
});

