$(document).ready(function() {
    // ƒ∞stek Durum G√∂stergesi
    $(document).ajaxStart(function() {
        updateStatus('ƒ∞stek g√∂nderiliyor...', 'pending');
    }).ajaxSuccess(function() {
        updateStatus('ƒ∞stek ba≈üarƒ±lƒ±!', 'success');
    }).ajaxError(function() {
        updateStatus('Hata olu≈ütu!', 'error');
    }).ajaxComplete(function() {
        setTimeout(() => {
            updateStatus('Hazƒ±r', 'ready');
        }, 2000);
    });

    // Blog Yazƒ±larƒ±
    $('#getPosts').click(function() {
        $.getJSON('https://jsonplaceholder.typicode.com/posts?_limit=3')
            .done(function(posts) {
                posts.forEach(function(post) {
                    const html = `
                        <div class="result-item success">
                            <h5>${post.title}</h5>
                            <p>${post.body}</p>
                            <small>Post ID: ${post.id}</small>
                        </div>
                    `;
                    $('#results').prepend(html);
                });
            })
            .fail(function(xhr, status, error) {
                showError(error);
            });
    });

    // Kullanƒ±cƒ±lar
    $('#getUsers').click(function() {
        $.getJSON('https://jsonplaceholder.typicode.com/users?_limit=3')
            .done(function(users) {
                users.forEach(function(user) {
                    const html = `
                        <div class="result-item success">
                            <h5>${user.name}</h5>
                            <p><strong>Email:</strong> ${user.email}</p>
                            <p><strong>Telefon:</strong> ${user.phone}</p>
                            <p><strong>Website:</strong> ${user.website}</p>
                            <small>Kullanƒ±cƒ± ID: ${user.id}</small>
                        </div>
                    `;
                    $('#results').prepend(html);
                });
            })
            .fail(function(xhr, status, error) {
                showError(error);
            });
    });

    // Fotoƒüraflar
    $('#getPhotos').click(function() {
        $.getJSON('https://jsonplaceholder.typicode.com/photos?_limit=3')
            .done(function(photos) {
                photos.forEach(function(photo) {
                    const html = `
                        <div class="result-item success">
                            <h5>${photo.title}</h5>
                            <img src="${photo.thumbnailUrl}" alt="${photo.title}">
                            <small>Fotoƒüraf ID: ${photo.id}</small>
                        </div>
                    `;
                    $('#results').prepend(html);
                });
            })
            .fail(function(xhr, status, error) {
                showError(error);
            });
    });

    // Temizle
    $('#clearResults').click(function() {
        $('#results').empty();
    });

    // Yardƒ±mcƒ± Fonksiyonlar
    function showError(error) {
        const html = `
            <div class="result-item error">
                <p class="error-message">Hata: ${error}</p>
            </div>
        `;
        $('#results').prepend(html);
    }

    function updateStatus(message, status) {
        const $status = $('#requestStatus');
        $status.attr('class', 'status-panel ' + status)
            .find('.status-text').text(message);
    }
});

