$(document).ready(function() {
    let normalCount = 0;
    let debounceCount = 0;
    let throttleCount = 0;

    // Normal olay i≈üleyici
    $('#searchInput').on('input', function() {
        normalCount++;
        updateCounter('normal', normalCount);
        simulateSearch($(this).val(), 'normal');
    });

    // Debounce ile olay i≈üleyici
    $('#searchInput').on('input', debounce(function() {
        debounceCount++;
        updateCounter('debounce', debounceCount);
        simulateSearch($(this).val(), 'debounce');
    }, 500));

    // Throttle ile olay i≈üleyici
    $('#searchInput').on('input', throttle(function() {
        throttleCount++;
        updateCounter('throttle', throttleCount);
        simulateSearch($(this).val(), 'throttle');
    }, 500));

    // Debounce fonksiyonu
    function debounce(func, wait) {
        let timeout;
        return function() {
            const context = this;
            const args = arguments;
            clearTimeout(timeout);
            timeout = setTimeout(() => {
                func.apply(context, args);
            }, wait);
        };
    }

    // Throttle fonksiyonu
    function throttle(func, limit) {
        let inThrottle;
        return function() {
            const args = arguments;
            const context = this;
            if (!inThrottle) {
                func.apply(context, args);
                inThrottle = true;
                setTimeout(() => inThrottle = false, limit);
            }
        };
    }

    // Saya√ßlarƒ± g√ºncelle
    function updateCounter(type, count) {
        $(`#${type}Count`).text(count);
    }

    // Arama sim√ºlasyonu
    function simulateSearch(query, type) {
        const resultDiv = $(`#${type}Result`);
        resultDiv.html(`<div class="searching">Aranƒ±yor: ${query}</div>`);

        // Ger√ßek bir API √ßaƒürƒ±sƒ±nƒ± sim√ºle et
        setTimeout(() => {
            if (query.trim() === '') {
                resultDiv.html('');
            } else {
                resultDiv.html(`
                    <div class="search-result">
                        <div class="result-item">Sonu√ß 1: ${query}_A</div>
                        <div class="result-item">Sonu√ß 2: ${query}_B</div>
                        <div class="result-item">Sonu√ß 3: ${query}_C</div>
                    </div>
                `);
            }
        }, 300);
    }
});

