$(document).ready(function() {
    // Durum g√ºncelleme fonksiyonu
    function updateStatus(message, status = '') {
        $('.status-panel')
            .attr('class', 'status-panel ' + status)
            .find('.status-text')
            .text(message);
    }

    // Efekt hƒ±zƒ±nƒ± al
    function getSpeed() {
        const speed = $('#speedSelect').val();
        return speed === '0' ? undefined : speed;
    }

    // Show butonu
    $('#showBtn').click(function() {
        const speed = getSpeed();
        updateStatus('G√∂steriliyor...', 'pending');

        $('#demoBox').show(speed, function() {
            updateStatus('Element g√∂sterildi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Hide butonu
    $('#hideBtn').click(function() {
        const speed = getSpeed();
        updateStatus('Gizleniyor...', 'pending');

        $('#demoBox').hide(speed, function() {
            updateStatus('Element gizlendi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Toggle butonu
    $('#toggleBtn').click(function() {
        const speed = getSpeed();
        const $box = $('#demoBox');
        const isVisible = $box.is(':visible');

        updateStatus(
            isVisible ? 'Gizleniyor...' : 'G√∂steriliyor...',
            'pending'
        );

        $box.toggle(speed, function() {
            updateStatus(
                isVisible ? 'Element gizlendi!' : 'Element g√∂sterildi!',
                'success'
            );
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });
});

