    // Durum g√ºncelleme fonksiyonu
    function updateStatus(message, status = '') {
        $('.status-panel')
            .attr('class', 'status-panel ' + status)
            .find('.status-text')
            .text(message);
    }

    // Efekt hƒ±zƒ±nƒ± al
    function getSpeed() {
        return $('#speedSelect').val();
    }

    // Fade In butonu
    $('#fadeInBtn').click(function() {
        const speed = getSpeed();
        updateStatus('Fade in efekti uygulanƒ±yor...', 'pending');

        $('#demoBox').fadeIn(speed, function() {
            updateStatus('Element g√∂sterildi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Fade Out butonu
    $('#fadeOutBtn').click(function() {
        const speed = getSpeed();
        updateStatus('Fade out efekti uygulanƒ±yor...', 'pending');

        $('#demoBox').fadeOut(speed, function() {
            updateStatus('Element gizlendi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Fade Toggle butonu
    $('#fadeToggleBtn').click(function() {
        const speed = getSpeed();
        const $box = $('#demoBox');
        const isVisible = $box.is(':visible');

        updateStatus(
            isVisible ? 'Fade out efekti uygulanƒ±yor...' : 'Fade in efekti uygulanƒ±yor...',
            'pending'
        );

        $box.fadeToggle(speed, function() {
            updateStatus(
                isVisible ? 'Element gizlendi!' : 'Element g√∂sterildi!',
                'success'
            );
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Opaklƒ±k deƒüi≈üimi
    $('#opacityRange').on('input', function() {
        const opacity = $(this).val() / 100;
        $('#opacityValue').text($(this).val() + '%');

        const speed = getSpeed();
