$(document).ready(function() {
    // Ba≈ülangƒ±√ßta accordion i√ßeriklerini gizle
    $('.accordion-content').hide();

    // Durum g√ºncelleme fonksiyonu
    function updateStatus(message, status = '') {
        $('.status-panel')
            .attr('class', 'status-panel ' + status)
            .find('.status-text')
            .text(message);
    }

    // Efekt ayarlarƒ±nƒ± al
    function getEffectSettings() {
        return {
            duration: $('#speedSelect').val(),
            easing: $('#easingSelect').val()
        };
    }

    // Slide Down butonu
    $('#slideDownBtn').click(function() {
        const settings = getEffectSettings();
        updateStatus('Slide down efekti uygulanƒ±yor...', 'pending');

        $('#demoBox').slideDown(settings, function() {
            updateStatus('Element g√∂sterildi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Slide Up butonu
    $('#slideUpBtn').click(function() {
        const settings = getEffectSettings();
        updateStatus('Slide up efekti uygulanƒ±yor...', 'pending');

        $('#demoBox').slideUp(settings, function() {
            updateStatus('Element gizlendi!', 'success');
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Slide Toggle butonu
    $('#slideToggleBtn').click(function() {
        const settings = getEffectSettings();
        const $box = $('#demoBox');
        const isVisible = $box.is(':visible');

        updateStatus(
            isVisible ? 'Slide up efekti uygulanƒ±yor...' : 'Slide down efekti uygulanƒ±yor...',
            'pending'
        );

        $box.slideToggle(settings, function() {
            updateStatus(
                isVisible ? 'Element gizlendi!' : 'Element g√∂sterildi!',
                'success'
            );
            setTimeout(() => updateStatus('Hazƒ±r'), 2000);
        });
    });

    // Accordion i≈ülevselliƒüi
    $('.accordion-header').click(function() {
        const $content = $(this).next('.accordion-content');
        const settings = getEffectSettings();

        // Diƒüer a√ßƒ±k i√ßerikleri kapat
        $('.accordion-content').not($content).slideUp(settings);
        $('.accordion-header').not(this).removeClass('active');

        // Tƒ±klanan i√ßeriƒüi a√ß/kapa
        $content.slideToggle(settings);
        $(this).toggleClass('active');
    });
});

