$(document).ready(function() {
    // Durum g√ºncelleme fonksiyonu
    function updateStatus(message, status = '') {
        $('.status-panel')
            .attr('class', 'status-panel ' + status)
            .find('.status-text')
            .text(message);
    }

    // Animasyon ayarlarƒ±nƒ± al
    function getAnimationSettings() {
        return {
            duration: parseInt($('#durationRange').val()),
            easing: $('#easingSelect').val(),
            queue: $('input[name="queue"]:checked').val() === 'true'
        };
    }

    // S√ºre deƒüerini g√ºncelle
    $('#durationRange').on('input', function() {
        $('#durationValue').text($(this).val() + 'ms');
    });

    // Hareket animasyonu
    $('#moveBtn').click(function() {
        const settings = getAnimationSettings();
        const $box = $('#demoBox');
        const stage = $('.animation-stage');
        const maxX = stage.width() - $box.width();
        const maxY = stage.height() - $box.height();

        updateStatus('Hareket animasyonu uygulanƒ±yor...', 'pending');

        $box.animate({
            left: Math.random() * maxX,
            top: Math.random() * maxY
        }, {
            ...settings,
            complete: function() {
                updateStatus('Hareket tamamlandƒ±!', 'success');
                setTimeout(() => updateStatus('Hazƒ±r'), 2000);
            }
        });
    });

    // Boyut animasyonu
    $('#sizeBtn').click(function() {
        const settings = getAnimationSettings();
        const $box = $('#demoBox');
        const isLarge = $box.width() > 200;

        updateStatus('Boyut animasyonu uygulanƒ±yor...', 'pending');

        $box.animate({
            width: isLarge ? '200px' : '300px',
            height: isLarge ? '200px' : '300px'
        }, {
            ...settings,
            complete: function() {
                updateStatus('Boyut deƒüi≈ütirildi!', 'success');
                setTimeout(() => updateStatus('Hazƒ±r'), 2000);
            }
        });
    });

    // Renk animasyonu
    $('#colorBtn').click(function() {
        const settings = getAnimationSettings();
        const $box = $('#demoBox');
        const colors = ['#2ecc71', '#3498db', '#e74c3c', '#f1c40f', '#9b59b6'];
        const randomColor = colors[Math.floor(Math.random() * colors.length)];

        updateStatus('Renk animasyonu uygulanƒ±yor...', 'pending');

        $box.animate({
            backgroundColor: randomColor
        }, {
            ...settings,
            complete: function() {
                updateStatus('Renk deƒüi≈ütirildi!', 'success');
                setTimeout(() => updateStatus('Hazƒ±r'), 2000);
            }
        });
    });

    // Sƒ±fƒ±rlama
    $('#resetBtn').click(function() {
        const settings = getAnimationSettings();
        const $box = $('#demoBox');

        updateStatus('Pozisyon sƒ±fƒ±rlanƒ±yor...', 'pending');

        $box.animate({
            left: '50%',
            top: '50%',
            width: '200px',
            height: '200px',
            backgroundColor: '#ffffff'
        }, {
            ...settings,
            complete: function() {
                updateStatus('Pozisyon sƒ±fƒ±rlandƒ±!', 'success');
                setTimeout(() => updateStatus('Hazƒ±r'), 2000);
            }
        });
    });
});

