queue_effects.js:
$(document).ready(function() {
    const $box = $('#animationBox');
    const $status = $('#status');
    const $queueStatus = $('#queueStatus');

    // Durum g√ºncelleme fonksiyonu
    function updateStatus(message) {
        $status.prepend(`<p>${new Date().toLocaleTimeString()}: ${message}</p>`);
    }

    // Kuyruk durumunu g√ºncelleme
    function updateQueueStatus() {
        const queueLength = $box.queue().length;
        $queueStatus.text(`Kuyrukta bekleyen animasyon sayƒ±sƒ±: ${queueLength}`);
    }

    // Sƒ±ralƒ± animasyon ba≈ülat
    $('#startSequence').click(function() {
        updateStatus('Sƒ±ralƒ± animasyon ba≈ülatƒ±ldƒ±');

        $box.animate({ width: '200px' }, 1000)
            .queue(function(next) {
                $(this).css('background-color', '#e74c3c');
                updateStatus('Renk deƒüi≈ütirildi');
                next();
            })
            .animate({ height: '200px' }, 1000)
            .queue(function(next) {
                $(this).css('border', '5px solid #f1c40f');
                updateStatus('Kenarlƒ±k eklendi');
                next();
            })
            .animate({ left: '150px' }, 1000);

        updateQueueStatus();
    });

    // √ñzel kuyruk ba≈ülat
    $('#startCustomQueue').click(function() {
        updateStatus('√ñzel kuyruk ba≈ülatƒ±ldƒ±');

        $box.queue('ozelKuyruk', function(next) {
            $(this).addClass('efekt1');
            updateStatus('Efekt 1 uygulandƒ±');
            setTimeout(next, 1000);
        }).queue('ozelKuyruk', function(next) {
            $(this).removeClass('efekt1').addClass('efekt2');
            updateStatus('Efekt 2 uygulandƒ±');
            setTimeout(next, 1000);
        }).queue('ozelKuyruk', function(next) {
            $(this).removeClass('efekt2').addClass('efekt3');
            updateStatus('Efekt 3 uygulandƒ±');
            setTimeout(next, 1000);
        }).queue('ozelKuyruk', function(next) {
            $(this).removeClass('efekt3');
            updateStatus('Efektler temizlendi');
            next();
        });

        $box.dequeue('ozelKuyruk');
    });

    // Kuyruƒüu temizle
    $('#clearQueue').click(function() {
        $box.clearQueue();
        $box.clearQueue('ozelKuyruk');
        updateStatus('T√ºm kuyruklar temizlendi');
        updateQueueStatus();
    });

    // Sƒ±fƒ±rla
    $('#reset').click(function() {
        $box.clearQueue()
            .clearQueue('ozelKuyruk')
            .removeAttr('style')
            .removeClass('efekt1 efekt2 efekt3');
        updateStatus('Kutu sƒ±fƒ±rlandƒ±');
        updateQueueStatus();
    });

    // ƒ∞lk durum g√ºncelleme
    updateQueueStatus();
});

