// Renk deƒüi≈ütirme eklentisi
$.fn.colorize = function(options) {
    const settings = $.extend({
        colors: ['#3498db', '#e74c3c', '#2ecc71', '#f1c40f'],
        interval: 1000,
        onClick: false
    }, options);

    return this.each(function() {
        const $element = $(this);
        let colorIndex = 0;
        let interval;

        function changeColor() {
            $element.css('background-color', settings.colors[colorIndex]);
            colorIndex = (colorIndex + 1) % settings.colors.length;
        }

        if (settings.onClick) {
            $element.on('click', changeColor);
        } else {
            interval = setInterval(changeColor, settings.interval);
            $element.data('colorInterval', interval);
        }
    });
};

// Animasyon eklentisi
$.fn.pulseAnimate = function(options) {
    const settings = $.extend({
        minScale: 0.8,
        maxScale: 1.2,
        duration: 1000,
        easing: 'swing'
    }, options);

    return this.each(function() {
        const $element = $(this);
        let isAnimating = false;
        const originalWidth = $element.width();
        const originalHeight = $element.height();

        function animate() {
            if (!isAnimating) return;

            const maxWidth = originalWidth * settings.maxScale;
            const maxHeight = originalHeight * settings.maxScale;

            $element.animate({
                width: maxWidth,
                height: maxHeight
            }, {
                duration: settings.duration,
                easing: settings.easing,
                complete: function() {
                    if (!isAnimating) return;

                    const minWidth = originalWidth * settings.minScale;
                    const minHeight = originalHeight * settings.minScale;

                    $element.animate({
                        width: minWidth,
                        height: minHeight
                    }, {
                        duration: settings.duration,
                        easing: settings.easing,
                        complete: animate
                    });
                }
            });
        }

        $element.data('pulseAnimate', {
            start: function() {
                isAnimating = true;
                animate();
            },
            stop: function() {
                isAnimating = false;
                $element.stop(true).animate({
                    width: originalWidth,
                    height: originalHeight
                });
            }
        });
    });
};

// Metin efekt eklentisi
$.fn.textEffect = function(options) {
    const settings = $.extend({
        delay: 50,
        className: 'highlight'
    }, options);

    return this.each(function() {
        const $element = $(this);
        const text = $element.text();
        let html = '';

        // Her karakteri span i√ßine al
        for (let i = 0; i < text.length; i++) {
            html += `<span style="opacity: 0">${text[i]}</span>`;
        }

        $element.html(html);

        // Karakterleri sƒ±rayla g√∂ster
        $element.find('span').each(function(i) {
            const $span = $(this);
            setTimeout(function() {
                $span.addClass(settings.className)
                     .css({
                         opacity: 1,
                         transform: 'translateY(0)',
                         transition: 'all 0.3s ease'
                     });
            }, i * settings.delay);
        });
    });
};

// Eklentileri kullan
$(document).ready(function() {
    // Renk deƒüi≈ütirme demo
    $('#colorBox').colorize({
        onClick: true
    });

    $('#applyColors').click(function() {
        $('#colorBox').colorize({
            interval: 500
        });
    });

    $('#resetColors').click(function() {
        const interval = $('#colorBox').data('colorInterval');
        if (interval) clearInterval(interval);
        $('#colorBox').css('background-color', '');
    });

    // Animasyon demo
    $('#animationBox').pulseAnimate();

    $('#startAnimation').click(function() {
        $('#animationBox').data('pulseAnimate').start();
    });

    $('#stopAnimation').click(function() {
        $('#animationBox').data('pulseAnimate').stop();
    });

    // Metin efekt demo
    $('#applyTextEffect').click(function() {
        $('#textEffect').textEffect();
    });
});

