$(document).ready(function() {
    // T√ºrk√ße dil desteƒüini etkinle≈ütir
    $.datepicker.setDefaults($.datepicker.regional['tr']);

    // Temel Datepicker
    $('#basicDate').datepicker({
        showOtherMonths: true,
        selectOtherMonths: true
    });

    // Geli≈ümi≈ü Datepicker
    $('#advancedDate').datepicker({
        dateFormat: 'dd.mm.yy',
        minDate: 0,
        maxDate: '+3M',
        showOtherMonths: true,
        selectOtherMonths: true,
        showButtonPanel: true,
        changeMonth: true,
        changeYear: true,
        yearRange: 'c-10:c+10',
        beforeShowDay: function(date) {
            const day = date.getDay();
            return [day !== 0 && day !== 6, ''];
        },
        onSelect: function(dateText) {
            console.log('Se√ßilen tarih:', dateText);
        }
    });

    // Tarih Aralƒ±ƒüƒ± Se√ßici
    let startDate, endDate;

    $('#startDate').datepicker({
        dateFormat: 'dd.mm.yy',
        onSelect: function(selectedDate) {
            startDate = $(this).datepicker('getDate');
            $("#endDate").datepicker("option", "minDate", selectedDate);
            updateDateInfo();
        }
    });

    $('#endDate').datepicker({
        dateFormat: 'dd.mm.yy',
        onSelect: function(selectedDate) {
            endDate = $(this).datepicker('getDate');
            $("#startDate").datepicker("option", "maxDate", selectedDate);
            updateDateInfo();
        }
    });

    function updateDateInfo() {
        if (startDate && endDate) {
            const days = Math.round((endDate - startDate) / (1000 * 60 * 60 * 24));
            $('#dateInfo').html(
                `<div class="info-box">
                    <p>Se√ßilen tarih aralƒ±ƒüƒ±: ${days} g√ºn</p>
                    <p>Ba≈ülangƒ±√ß: ${$.datepicker.formatDate('DD, d MM yy', startDate)}</p>
                    <p>Biti≈ü: ${$.datepicker.formatDate('DD, d MM yy', endDate)}</p>
                </div>`
            );
        }
    }

    // √ñzel Formatlar
    $('#format1').datepicker({
        dateFormat: 'dd.mm.yy'
    });

    $('#format2').datepicker({
        dateFormat: 'DD, d MM yy'
    });

    $('#format3').datepicker({
        dateFormat: 'yy-mm-dd'
    });
});

