$(document).ready(function() {
    // Grid √∂ƒüelerinin y√ºklenmesini bekle
    var $grid = $('.grid').imagesLoaded(function() {
        // Masonry'yi ba≈ülat
        $grid.masonry({
            itemSelector: '.grid-item',
            columnWidth: '.grid-sizer',
            gutter: '.gutter-sizer',
            percentPosition: true,
            horizontalOrder: true,
            transitionDuration: '0.4s'
        });
    });

    // Yeni √∂ƒüe ekleme fonksiyonu
    $('#addItems').click(function() {
        var items = generateItems(3); // 3 yeni √∂ƒüe olu≈ütur
        var $items = $(items);

        $items.imagesLoaded(function() {
            $grid.append($items)
                .masonry('appended', $items)
                .masonry('layout');
        });
    });

    // √ñƒüeleri karƒ±≈ütƒ±rma
    $('#shuffleItems').click(function() {
        $grid.children('.grid-item').each(function() {
            $(this).css({
                'order': Math.floor(Math.random() * 12)
            });
        });
        $grid.masonry('layout');
    });

    // Filtreleme
    $('.filter-buttons button').click(function() {
        var filterValue = $(this).attr('data-filter');

        // Aktif buton stilini g√ºncelle
        $('.filter-buttons button').removeClass('active');
        $(this).addClass('active');

        if (filterValue === '*') {
            // T√ºm √∂ƒüeleri g√∂ster
            $grid.children('.grid-item').show();
        } else {
            // Se√ßili kategorideki √∂ƒüeleri g√∂ster
            $grid.children('.grid-item').hide();
            $grid.children(filterValue).show();
        }

        $grid.masonry('layout');
    });

    // Yeni √∂ƒüe olu≈üturma fonksiyonu
    function generateItems(count) {
        var items = '';
        var categories = ['nature', 'city', 'abstract'];
        var sizes = ['', 'wide', 'tall', 'wide tall'];

        for (var i = 0; i < count; i++) {
            var category = categories[Math.floor(Math.random() * categories.length)];
            var size = sizes[Math.floor(Math.random() * sizes.length)];
            var randomNum = Math.floor(Math.random() * 100);

            items += `
                <div class="grid-item ${category} ${size}">
                    <img src="https://picsum.photos/300/200?random=${randomNum}" alt="${category} ${randomNum}">
                    <div class="item-content">
                        <h3>${category.charAt(0).toUpperCase() + category.slice(1)} ${randomNum}</h3>
                        <p>Yeni eklenen ${category} √∂ƒüesi</p>
                    </div>
                </div>
            `;
        }

        return items;
    }
});

