        $('.highlight').removeClass('highlight');
        $elements.addClass('highlight');

        // Se√ßili element bilgisini g√ºncelle
        const count = $elements.length;
        const info = count > 0 
            ? `${count} element se√ßildi: ${$elements.map(function() {
                return $(this).prop('tagName').toLowerCase() + 
                       (this.id ? '#' + this.id : '') + 
                       (this.className ? '.' + this.className.replace(/highlight/g, '').trim().replace(/\s+/g, '.') : '');
              }).get().join(', ')}`
            : 'Hen√ºz bir se√ßim yapƒ±lmadƒ±.';

        $('#selectionInfo').text(info);
    }

    // Aktif men√º √∂ƒüesini referans noktasƒ± olarak kullan
    const $activeItem = $('.menu-item.active');

    // T√ºm karde≈üleri vurgula
    $('#highlightAll').click(function() {
        const $elements = $activeItem.siblings();
        highlightElements($elements);
    });

    // Sonraki elementi vurgula
    $('#highlightNext').click(function() {
        const $elements = $activeItem.next();
        highlightElements($elements);
    });

    // √ñnceki elementi vurgula
    $('#highlightPrev').click(function() {
        const $elements = $activeItem.prev();
        highlightElements($elements);
    });

    // Sonraki t√ºm elementleri vurgula
    $('#highlightNextAll').click(function() {
        const $elements = $activeItem.nextAll();
        highlightElements($elements);
    });

    // √ñnceki t√ºm elementleri vurgula
    $('#highlightPrevAll').click(function() {
        const $elements = $activeItem.prevAll();
        highlightElements($elements);
    });

    // √ñzel elementleri vurgula
    $('#highlightSpecial').click(function() {
        const $elements = $activeItem.siblings('.special');
        highlightElements($elements);
    });

    // Highlight'larƒ± sƒ±fƒ±rla
    $('#resetHighlight').click(function() {
        $('.highlight').removeClass('highlight');
        $('#selectionInfo').text('Hen√ºz bir se√ßim yapƒ±lmadƒ±.');
    });

    // Akordeon i≈ülevselliƒüi
    $('.accordion-header').click(function() {
        // Tƒ±klanan ba≈ülƒ±ƒüƒ±n i√ßeriƒüini a√ß/kapat
        $(this).next('.accordion-content').slideToggle();

        // Diƒüer t√ºm i√ßerikleri kapat
        $(this).parent().siblings().find('.accordion-content').slideUp();

        // Aktif sƒ±nƒ±fƒ±nƒ± g√ºncelle
        $(this).parent().addClass('active');
        $(this).parent().siblings().removeClass('active');
    });

    // ƒ∞lk akordeon √∂ƒüesini a√ßƒ±k olarak ba≈ülat
    $('.accordion-item:first-child').addClass('active');
    $('.accordion-item:first-child .accordion-content').show();
});

