$(document).ready(function() {
    // Highlight fonksiyonu
    function highlightElements($elements, targetInfo = '#selectionInfo') {
        $('.highlight').removeClass('highlight');
        $elements.addClass('highlight');

        // Se√ßili element bilgisini g√ºncelle
        const count = $elements.length;
        const info = count > 0 
            ? `${count} element se√ßildi: ${$elements.map(function() {
                return $(this).text().trim();
              }).get().join(', ')}`
            : 'Hen√ºz bir se√ßim yapƒ±lmadƒ±.';

        $(targetInfo).text(info);
    }

    // ƒ∞ndeks tabanlƒ± filtreleme
    $('#selectFirst').click(function() {
        const $elements = $('.item-list .item').first();
        highlightElements($elements);
    });

    $('#selectLast').click(function() {
        const $elements = $('.item-list .item').last();
        highlightElements($elements);
    });

    $('#selectEven').click(function() {
        const $elements = $('.item-list .item').filter(':even'); // 0, 2, 4, ...
        highlightElements($elements);
    });

    $('#selectOdd').click(function() {
        const $elements = $('.item-list .item').filter(':odd'); // 1, 3, 5, ...
        highlightElements($elements);
    });

    $('#selectByIndex').click(function() {
        const $elements = $('.item-list .item').eq(2); // 3. √∂ƒüe (indeks: 2)
        highlightElements($elements);
    });

    $('#selectSlice').click(function() {
        const $elements = $('.item-list .item').slice(2, 6); // 3-6 arasƒ± √∂ƒüeler
        highlightElements($elements);
    });

    // ƒ∞√ßerik tabanlƒ± filtreleme
    $('#selectSpecial').click(function() {
        const $elements = $('.item-list .item').filter('.special');
        highlightElements($elements);
    });

    $('#selectFruits').click(function() {
        const $elements = $('.item-list .item').filter('[data-category="fruit"]');
        highlightElements($elements);
    });

    $('#selectVegetables').click(function() {
        const $elements = $('.item-list .item').filter('[data-category="vegetable"]');
        highlightElements($elements);
    });

    $('#selectContains').click(function() {
        const $elements = $('.item-list .item').filter(function() {
            return $(this).text().toLowerCase().includes('a');
        });
        highlightElements($elements);
    });

    $('#selectLongText').click(function() {
        const $elements = $('.item-list .item').filter(function() {
            return $(this).text().length > 6;
        });
        highlightElements($elements);
    });

    // Hari√ß tutma
    $('#selectNotSpecial').click(function() {
        const $elements = $('.item-list .item').not('.special');
        highlightElements($elements);
    });

    $('#selectNotFirst').click(function() {
        const $elements = $('.item-list .item').not(':first');
        highlightElements($elements);
    });

    $('#selectNotFruits').click(function() {
        const $elements = $('.item-list .item').not('[data-category="fruit"]');
        highlightElements($elements);
    });

    // Zincirleme filtreleme
    $('#chainFilters').click(function() {
        const $elements = $('.item-list .item')
            .filter('.special')
            .filter('[data-category="fruit"]');
        highlightElements($elements);
    });

    $('#complexFilter').click(function() {
        const $elements = $('.item-list .item')
            .filter(':visible')
            .not('.special')
            .filter(function() {
                return $(this).text().length < 7;
            })
            .slice(0, 2);
        highlightElements($elements);
    });

    // Tablo filtreleme
    $('#tableEvenRows').click(function() {
        const $elements = $('.data-table tbody tr:even'); // 0, 2, 4, ...
        highlightElements($elements);
    });

    $('#tableOddRows').click(function() {
        const $elements = $('.data-table tbody tr:odd'); // 1, 3, 5, ...
        highlightElements($elements);
    });

    $('#tableElectronics').click(function() {
        const $elements = $('.data-table tbody tr').filter(function() {
            return $(this).find('td:eq(2)').text() === 'Elektronik';
        });
        highlightElements($elements);
    });

    $('#tableExpensive').click(function() {
        const $elements = $('.data-table tbody tr').filter(function() {
            return parseInt($(this).data('price')) > 10;
        });
        highlightElements($elements);
    });

    $('#tableSpecialRows').click(function() {
        const $elements = $('.data-table tbody tr.special');
        highlightElements($elements);
    });

    // Sƒ±fƒ±rlama
    $('#resetHighlight, #tableResetHighlight').click(function() {
        $('.highlight').removeClass('highlight');
        $('#selectionInfo').text('Hen√ºz bir se√ßim yapƒ±lmadƒ±.');
    });
});

