$(document).ready(function() {
    // Temel Klonlama √ñrnekleri
    $('#cloneSimple').click(function() {
        const $clone = $('#box1').clone();
        $clone.appendTo('#cloneContainer1');
    });

    $('#cloneWithEvents').click(function() {
        const $clone = $('#box1').clone(true); // true: olaylarƒ± da klonla
        $clone.appendTo('#cloneContainer1');
    });

    $('#cloneAndModify').click(function() {
        const $clone = $('#box1').clone();
        $clone
            .css('background-color', getRandomColor())
            .find('h4')
            .text('Deƒüi≈ütirilmi≈ü Klon');
        $clone.appendTo('#cloneContainer1');
    });

    // Geli≈ümi≈ü Klonlama √ñrnekleri
    $('#cloneDeep').click(function() {
        const $clone = $('#box2').clone(true, true); // deep clone
        setupCloneEvents($clone);
        $clone.appendTo('#cloneContainer2');
    });

    $('#cloneMultiple').click(function() {
        const count = 3;
        for (let i = 0; i < count; i++) {
            const $clone = $('#box2').clone(true, true);
            setupCloneEvents($clone);
            $clone
                .css({
                    'transform': `scale(${0.8 - i * 0.1})`,
                    'margin-left': `${i * 20}px`
                })
                .appendTo('#cloneContainer2');
        }
    });

    $('#clonePositioned').click(function() {
        const $clone = $('#box2').clone(true, true);
        setupCloneEvents($clone);

        const offset = $('#box2').offset();
        $clone
            .css({
                'position': 'absolute',
                'top': offset.top + 20,
                'left': offset.left + 20
            })
            .appendTo('#cloneContainer2');
    });

    // Form Klonlama
    $('#addFormGroup').click(function() {
        const $clone = $('.form-group:first').clone(true);
        $clone
            .find('input')
            .val('')
            .end()
            .appendTo('#dynamicForm');
    });

    // Form olaylarƒ±
    $(document).on('click', '.btn-remove', function() {
        const $formGroup = $(this).closest('.form-group');
        if ($('.form-group').length > 1) {
            $formGroup.slideUp(300, function() {
                $(this).remove();
            });
        }
    });

    $('#submitForm').click(function() {
        const formData = [];
        $('.form-group').each(function() {
            formData.push({
                name: $(this).find('.input-name').val(),
                email: $(this).find('.input-email').val()
            });
        });
        console.log('Form verileri:', formData);
    });

    // Yardƒ±mcƒ± fonksiyonlar
    function setupCloneEvents($clone) {
        $clone.find('.input-demo').on('input', function() {
            const count = $(this).val().length;
            $(this).siblings('.counter').text(`Karakter sayƒ±sƒ±: ${count}`);
        });

        $clone.find('.btn-action').click(function() {
            $(this).siblings('.input-demo').val('');
            $(this).siblings('.counter').text('Karakter sayƒ±sƒ±: 0');
        });
    }

    function getRandomColor() {
        const letters = '0123456789ABCDEF';
        let color = '#';
        for (let i = 0; i < 6; i++) {
            color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
    }

    // ƒ∞lk kutu i√ßin olay dinleyicileri
    $('#box1 .btn-action').click(function() {
        alert('Orijinal d√ºƒümeye tƒ±klandƒ±!');
    });

    // ƒ∞kinci kutu i√ßin olay dinleyicileri
    setupCloneEvents($('#box2'));
});

