$(document).ready(function() {
    // Element deƒüi≈ütirme √∂rnekleri
    let replaceCount = 1;

    $('#replaceElement').click(function() {
        const newElement = $('<div>', {
            class: 'target-element',
            html: `<p>Yeni Element ${replaceCount++}</p>`
        });

        $('#target1').replaceWith(newElement);
    });

    $('#replaceHTML').click(function() {
        $('#target1').html(`
            <h4>Deƒüi≈ütirilmi≈ü ƒ∞√ßerik ${replaceCount++}</h4>
            <p>Bu i√ßerik HTML olarak deƒüi≈ütirildi.</p>
        `);
    });

    $('#replaceAll').click(function() {
        const newContent = `<div class="replaced-content">Toplu Deƒüi≈ütirilmi≈ü ${replaceCount++}</div>`;
        $(newContent).replaceAll('.target-element');
    });

    $('#resetReplace').click(function() {
        location.reload();
    });

    // Element sarmalama √∂rnekleri
    $('#wrapSingle').click(function() {
        $('#item2').wrap('<div class="wrapper" style="border: 2px solid #3498db; padding: 10px; margin: 5px;"></div>');
    });

    $('#wrapAll').click(function() {
        $('.wrap-item').wrapAll('<div class="group-wrapper" style="border: 2px dashed #e74c3c; padding: 15px; margin: 10px;"></div>');
    });

    $('#wrapInner').click(function() {
        $('.wrap-item').wrapInner('<div class="inner-wrapper" style="background: #f1c40f; padding: 5px;"></div>');
    });

    $('#unwrap').click(function() {
        $('.wrap-item').unwrap();
    });

    $('#resetWrap').click(function() {
        location.reload();
    });

    // Dinamik i√ßerik deƒüi≈ütirme
    let contentCount = 2;

    $('#addContent').click(function() {
        const newContent = $('<div>', {
            class: 'content-item',
            html: `
                <h4>Ba≈ülƒ±k ${contentCount}</h4>
                <p>ƒ∞√ßerik ${contentCount}</p>
            `
        });

        $('.content-container').append(newContent);
        contentCount++;
    });

    $('#replaceContent').click(function() {
        $('.content-item.active').replaceWith(`
            <div class="content-item active">
                <h4>Deƒüi≈ütirilmi≈ü Ba≈ülƒ±k</h4>
                <p>Bu i√ßerik tamamen deƒüi≈ütirildi.</p>
            </div>
        `);
    });

    $('#wrapContent').click(function() {
        $('.content-item').each(function(index) {
            $(this).wrap(`<div class="content-wrapper level-${index + 1}"></div>`);
        });
    });

    $('#resetContent').click(function() {
        location.reload();
    });
});

