localstorage.js:
$(document).ready(function() {
    // Sayfa y√ºklendiƒüinde tercihleri y√ºkle
    loadPreferences();
    loadNotes();
    updateStorageInfo();

    // Tercihleri kaydetme
    $('#savePreferences').click(function() {
        const preferences = {
            theme: $('#themeSelect').val(),
            fontSize: $('#fontSizeSelect').val(),
            lastUpdated: new Date().toISOString()
        };

        try {
            localStorage.setItem('userPreferences', JSON.stringify(preferences));
            applyPreferences(preferences);
            showMessage('Tercihler kaydedildi!');
        } catch (e) {
            showMessage('Hata: Tercihler kaydedilemedi!', 'error');
        }
    });

    // Not ekleme
    $('#saveNote').click(function() {
        const title = $('#noteTitle').val().trim();
        const content = $('#noteContent').val().trim();

        if (!title || !content) {
            showMessage('Ba≈ülƒ±k ve i√ßerik gerekli!', 'error');
            return;
        }

        const note = {
            id: Date.now(),
            title: title,
            content: content,
            created: new Date().toISOString()
        };

        try {
            const notes = JSON.parse(localStorage.getItem('notes') || '[]');
            notes.push(note);
            localStorage.setItem('notes', JSON.stringify(notes));

            addNoteToList(note);
            clearNoteForm();
            showMessage('Not eklendi!');
            updateStorageInfo();
        } catch (e) {
            showMessage('Hata: Not eklenemedi!', 'error');
        }
    });

    // Not silme
    $(document).on('click', '.delete-note', function() {
        const noteId = $(this).data('id');
        const notes = JSON.parse(localStorage.getItem('notes') || '[]');
        const updatedNotes = notes.filter(note => note.id !== noteId);

        localStorage.setItem('notes', JSON.stringify(updatedNotes));
        $(this).closest('.note-item').fadeOut(300, function() {
            $(this).remove();
            updateStorageInfo();
        });
    });

    // Verileri dƒ±≈üa aktarma
    $('#exportData').click(function() {
        const exportData = {
            preferences: JSON.parse(localStorage.getItem('userPreferences') || '{}'),
            notes: JSON.parse(localStorage.getItem('notes') || '[]')
        };

        const dataStr = JSON.stringify(exportData, null, 2);
        const blob = new Blob([dataStr], { type: 'application/json' });
        const url = URL.createObjectURL(blob);

        const a = document.createElement('a');
        a.href = url;
        a.download = 'my-app-data.json';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    });

    // T√ºm verileri temizleme
    $('#clearData').click(function() {
        if (confirm('T√ºm veriler silinecek. Emin misiniz?')) {
            localStorage.clear();
            $('#notesList').empty();
            loadPreferences(); // Varsayƒ±lan tercihleri y√ºkle
            updateStorageInfo();
            showMessage('T√ºm veriler temizlendi!');
        }
    });

    // Yardƒ±mcƒ± fonksiyonlar
    function loadPreferences() {
        const preferences = JSON.parse(localStorage.getItem('userPreferences') || '{}');
        if (preferences.theme) {
            $('#themeSelect').val(preferences.theme);
            $('#fontSizeSelect').val(preferences.fontSize);
            applyPreferences(preferences);
        }
    }

    function applyPreferences(preferences) {
        $('body').removeClass('theme-light theme-dark theme-sepia')
                .addClass('theme-' + preferences.theme)
                .css('font-size', getFontSize(preferences.fontSize));
    }

    function getFontSize(size) {
        const sizes = {
            small: '14px',
            medium: '16px',
            large: '18px'
        };
        return sizes[size] || sizes.medium;
    }

    function loadNotes() {
        const notes = JSON.parse(localStorage.getItem('notes') || '[]');
        notes.forEach(note => addNoteToList(note));
    }

    function addNoteToList(note) {
        const noteHtml = `
            <div class="note-item" data-id="${note.id}">
                <h4>${escapeHtml(note.title)}</h4>
                <p>${escapeHtml(note.content)}</p>
                <div class="note-meta">
                    <span class="note-date">${new Date(note.created).toLocaleDateString()}</span>
                    <button class="delete-note" data-id="${note.id}">Sil</button>
                </div>
            </div>
        `;
        $('#notesList').prepend(noteHtml);
    }

    function clearNoteForm() {
        $('#noteTitle').val('');
        $('#noteContent').val('');
    }

    function updateStorageInfo() {
        const totalSpace = 5 * 1024 * 1024; // 5MB (tipik localStorage limiti)
        const usedSpace = new Blob([JSON.stringify(localStorage)]).size;
        const usedKB = (usedSpace / 1024).toFixed(2);

        $('#usedSpace').text(usedKB + ' KB');
        $('#storageWarning').toggle(usedSpace > totalSpace * 0.8);
    }

    function showMessage(message, type = 'success') {
        const $msg = $('<div>')
            .addClass('message ' + type)
            .text(message)
            .appendTo('body')
            .fadeIn();

        setTimeout(() => $msg.fadeOut(() => $msg.remove()), 3000);
    }

    function escapeHtml(text) {
        const div = document.createElement('div');
        div.textContent = text;
        return div.innerHTML;
    }
});

